<?php class Fire_API{public static $API_VERSION="1.1";private $API_UPDATE_PAGE="https://api.fire-softwares.ga/?page=download";function updatesCheck(){$updater=new API;try{if($updater->getServerInformation(ServerName::$FIRE_SOFTWARES,ServerInfoType::$ONLINE)===TRUE){$lastversion=$updater->getServerInformation(ServerName::$API_STATUS,ServerInfoType::$VERSION);if(doubleval($lastversion)>doubleval($API_VERSION))$this->console_log(Messages::$API_WARNING_PREFIX." ".str_replace("%api-update-page%",$API_UPDATE_PAGE,str_replace("%new-version%",$lastversion,Messages::$API_UPDATE_AVAILABLE)),'warn');elseif(doubleval($lastversion)<doubleval($API_VERSION))$this->console_log(Messages::$API_WARNING_PREFIX." ".str_replace("%api-update-page%",$API_UPDATE_PAGE,str_replace("%lastest-stable%",$lastversion,str_replace("%api-version%",$API_VERSION,Messages::$API_BETA))),'warn');else $this->console_log(Messages::$API_PREFIX." ".str_replace("%api-version%",$API_VERSION,Messages::$API_UP_TO_DATE));}}catch(Exception $e){$this->console_log(Messages::$API_ERROR_PREFIX." ".Messages::$UPDATE_CHECK_EXCEPTION,'error');$this->console_log(Messages::$API_ERROR_PREFIX." ".$e->getMessage(),'error');}$this->console_log("\n\r".Messages::$API_PREFIX." ".str_replace("%api-version%",$API_VERSION,Messages::$API_HELLO));}function __construct(){$this->updatesCheck();}function console_log($output,$command='log',$with_script_tags=true){$js_code='console.'.$command.'('.json_encode($output,JSON_HEX_TAG).');';if($with_script_tags)$js_code='<script>'.$js_code.'</script>';echo $js_code;}}class Messages{public static $UPDATE_CHECK_EXCEPTION="Can't check for updates :";public static $API_PREFIX="[Fire-API]";public static $API_ERROR_PREFIX="[Fire-API Error]";public static $API_WARNING_PREFIX="[Fire-API Warning]";public static $API_HELLO="Fire-API version %api-version% by Fire-Softwares successfully loaded !";public static $API_UP_TO_DATE="You're running the lastest version of Fire-API (%api-version%), nice !";public static $API_BETA="Warning : you are using a beta / unstable build of Fire-API (%api-version%). If you don't know why you see this warning, go back to lastest stable version (%lastest-stable%) on %api-update-page%. Or else you're in the future of Fire-API 8)";public static $API_UPDATE_AVAILABLE="New update available : Fire-API version %new-version%. You can get the update here : %api-update-page%";}class ServerName{public static $FIRE_SOFTWARES=0;public static $FIRE_NETWORK=1;public static $MUMBLE=2;public static $MUMBLE_CVP=996;public static $DISCORD=997;public static $FRAMEWORK_STATUS=998;public static $API_STATUS=999;}class ServerInfoType{public static $CHANNELS=0;public static $CPU_LOAD_0=1;public static $CPU_LOAD_1=2;public static $CPU_LOAD_2=3;public static $DISCORD_ID=4;public static $DISK_MAX=5;public static $DISK_PERCENT=6;public static $DISK_UNIT=7;public static $DISK_USED=8;public static $HOSTNAME=9;public static $MEMBERS=10;public static $MEMBERS_ONLINE=11;public static $MUMBLE_X_CONNECT_URL=12;public static $ONLINE=13;public static $PLAYER_COUNT=14;public static $RAM_MAX=15;public static $RAM_PERCENT=16;public static $RAM_UNIT=17;public static $RAM_USED=18;public static $SERVER_NAME=19;public static $SERVER_STATUS=20;public static $UPTIME=21;public static $VERSION=22;}class API{private function getServerStatusURL($server_name){switch($server_name){case ServerName::$FIRE_SOFTWARES:return Server::$STATUS_0;case ServerName::$FIRE_NETWORK:return Server::$STATUS_1;case ServerName::$MUMBLE:return Server::$STATUS_2;case ServerName::$MUMBLE_CVP:return Server::$STATUS_MUMBLE_CVP;case ServerName::$DISCORD:return Server::$STATUS_DISCORD;case ServerName::$FRAMEWORK_STATUS:return Server::$STATUS_FRAMEWORK;case ServerName::$API_STATUS:return Server::$STATUS_API;}}public function getServerInformation($server_name,$server_info_type){$link=$this->getServerStatusURL($server_name);switch($server_name){case ServerName::$FIRE_SOFTWARES:case ServerName::$FIRE_NETWORK:switch($server_info_type){case ServerInfoType::$CPU_LOAD_0:return json_decode(file_get_contents($link),true)['status']['cpu'][0];case ServerInfoType::$CPU_LOAD_1:return json_decode(file_get_contents($link),true)['status']['cpu'][1];case ServerInfoType::$CPU_LOAD_2:return json_decode(file_get_contents($link),true)['status']['cpu'][2];case ServerInfoType::$DISK_MAX:return json_decode(file_get_contents($link),true)['status']['disk']['total'];case ServerInfoType::$DISK_PERCENT:return json_decode(file_get_contents($link),true)['status']['disk']['percent'];case ServerInfoType::$DISK_UNIT:return json_decode(file_get_contents($link),true)['status']['units']['disk'];case ServerInfoType::$DISK_USED:return json_decode(file_get_contents($link),true)['status']['disk']['used'];case ServerInfoType::$HOSTNAME:return json_decode(file_get_contents($link),true)['status']['hostname'];case ServerInfoType::$ONLINE:return json_decode(file_get_contents($link),true)['status']['online'];case ServerInfoType::$RAM_MAX:return json_decode(file_get_contents($link),true)['status']['ram']['total'];case ServerInfoType::$RAM_PERCENT:return json_decode(file_get_contents($link),true)['status']['ram']['percent'];case ServerInfoType::$RAM_UNIT:return json_decode(file_get_contents($link),true)['status']['units']['ram'];case ServerInfoType::$RAM_USED:return json_decode(file_get_contents($link),true)['status']['ram']['used'];default:die('<b>Fire-API Error : </b>Unknown server information type.<br>See <a href="https://api.fire-softwares.ga/" target="_blank">Fire-API documentation</a> for more help.');break;}case ServerName::$MUMBLE:switch($server_info_type){case ServerInfoType::$ONLINE:return json_decode(file_get_contents($link),true)['status']['online'];case ServerInfoType::$PLAYER_COUNT:return json_decode(file_get_contents($link),true)['status']['players']['online'];}case ServerName::$MUMBLE_CVP:switch($server_info_type){case ServerInfoType::$CHANNELS:return json_decode(file_get_contents($link),true)['root']['channel